mapper.select:
select t.id from compare_source t where 1 = 1 and t.status = '0'
and t.id >= ${startKeyCode}
order by t.id asc

select distinct t.key_code as id from compare_list t where 1 = 1 and t.status = '0'
and t.key_code >= ${startKeyCode}
order by t.key_code asc

select t.id from compare_result t where 1 = 1 and t.status = '0'
and t.id >= ${startKeyCode}
order by t.id asc


        select v.id 
          from  ( 
	                 select type, prp_code as id from 
	                 					(

	          					 	 	)
          		 	) v
        where 1 = 1


            and v.id  >=   ${startKeyCode}

         order by v.id asc

   select
		   prp_code,
		   type
    from ( 

     ) t
   where 1=1 
 	 	 and t.prp_code   >=   ${startKeyCode}
     and t.prp_code   <=   ${endKeyCode}

select distinct t.prp_code as id from prp_rule_check_result t where 1=1 and t.is_restore = 0 and nvl(t.status, -1) != 2
and t.prp_code >= ${startKeyCode}
order by t.prp_code asc
